package com.tongfu.mytestapp.dagger2.advanceduse2;import android.app.ActivityManager;import android.content.Context;import android.content.Intent;import dagger.Module;import dagger.Provides;@Modulepublic class AdvancedUseModule2 {    @Provides    public ActivityManager provideActivityManager(Context context){        return  (ActivityManager)context.getSystemService(Context.ACTIVITY_SERVICE);    }}