package com.tongfu.mytestapp.imageload;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.tongfu.mytestapp.R;import butterknife.ButterKnife;import butterknife.OnClick;public class ImageLoadEntryActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_image_load_entry);        ButterKnife.bind(this);    }    @OnClick(R.id.btn_picasso)    void onBtnPicassoClicked(){        Intent intent = new Intent(this , PicassoActivity.class);        startActivity(intent);    }}